INSERT INTO `wp_posts` VALUES("853", "1", "2020-01-29 17:21:17", "2020-01-29 16:21:17", "<!-- wp:paragraph -->\r\n<h2>Un site pour créer du lien à l\'école.</h2>\r\n<p><!-- wp:paragraph --></p>\r\n<p>  Ce site  est un blog d\'information à destination des parents d\'élèves de Saint Macaire.</p>\r\n<p>Son objectif est de fournir une information efficace à chacun sur la vie des écoles de Saint Macaire.</p>\r\n<ul>\r\n<li>\r\n<p>Fournir des informations pratiques</p>\r\n</li>\r\n<li>\r\n<p>Informer des débats en conseil d\'école et des enjeux</p>\r\n</li>\r\n<li>Informer des manifestations en rapport avec l\'Ecole</li>\r\n<li>Améliorer le \"vivre-ensemble\"</li>\r\n<li>Permettre la participation de chacun, à son niveau.</li>\r\n<li>Créer du lien social</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>Vous y aurez accès aux différentes actualités, aux dates des manifestations, ainsi qu\'à des articles sur divers sujets liés à l\'école.</p>\r\n<p>Bonne visite!</p>\r\n<p><!-- /wp:paragraph -->\r\n\r\n<!-- wp:paragraph --></p>\r\n<p>&nbsp;</p>\r\n<p><!-- /wp:paragraph --></p>\r\n<!-- /wp:paragraph -->", "accueil", "", "inherit", "closed", "closed", "", "6-revision-v1", "", "", "2020-01-29 17:21:17", "2020-01-29 16:21:17", "", "6", "http://localhost:8000/6-revision-v1/", "0", "revision", "", "0");
